+          <div class="task-header">
+          {% if task.user == app.user %}
+          {% if not task.isdone %}
+        <a href="{{ path('task_complete', { 'id': task.id, '_csrf_token': csrf_token('task_complete' ~ task.id) }) }}" style="margin-left: 700px; font-size: 24px; line-height: 24px; display: inline-block; width: 24px; height: 24px; text-align: center;">☐</a>
+
+
+     {% else %}
+    <a href="{{ path('task_incomplete', {'id': task.id, '_csrf_token': csrf_token('task_complete' ~ task.id)}) }}" style="margin-left: 700px; font-size: 24px; line-height: 24px; display: inline-block; width: 24px; height: 24px; text-align: center;">☑</a>
+
+     {% endif %}
+    {% endif %}
+            <h2 class="task-title" >{{ task.title }}</h2>
+          </div>
+          <div class="task-meta">
+          <span class="task-category">Categorie:{{ task.category.nom }}</span>
+            <span class="task-user">Postée par : <strong>{{ task.user.nom }}</strong></span>
+          </div>
+        
+<div class="container2">
+<div class="task-description">{{ task.description }}</div>
+</div>
+          <div class="task-actions">
+            <a href="{{ path('showTask', { id: task.id }) }}" class="btn btn-primary btn-sm">Lire la suite</a>
+            {% if task.user == app.user %}
+              <a href="{{ path('edit', { id: task.id }) }}" class="btn btn-success btn-sm">Modifier ma tache</a>
+            
+        {% endif %}
+    <span style="margin-left:250px ;" class="task-deadline">A faire pour le : {{ task.deadline|date('d/m/Y') }}</span>
+          </div>
+        </div>
+      {% endfor %}
+    </div>
+  </div>
+
+  <style>
+    .mark-completed {
+      background-color: #28a745;
+      color: #fff;
+    }
